jQuery(function($){var canBeLoaded=!0,bottomOffset=2000,maindiv=bliss_loadmore_params.maindiv,innerdiv=bliss_loadmore_params.innerdiv,loadmore_image=bliss_loadmore_params.loadmore_image;$(window).scroll(function(){var data={'action':'loadmore','query':bliss_loadmore_params.posts,'page':bliss_loadmore_params.current_page};if($(document).scrollTop()>($(document).height()-bottomOffset)&&canBeLoaded===!0){$.ajax({url:bliss_loadmore_params.ajaxurl,data:data,type:'POST',beforeSend:function(xhr){$(maindiv).parent().append('<span class="bliss-loading"><img src="'+loadmore_image+'"></span>');canBeLoaded=!1},success:function(data){if(data){$(maindiv).find(innerdiv+':last-of-type').after(data);$(maindiv).parent().find('.bliss-loading').remove();canBeLoaded=!0;bliss_loadmore_params.current_page++}else{$(maindiv).parent().find('.bliss-loading').remove()}}})}})})